import React, { useState } from "react";
import { Link } from "react-scroll";
import $ from "jquery";
import "../css/Home.css";
import Header from "../components/Header";
import "https://platform.twitter.com/widgets.js";
import RS from "../img/RuneScape In C.png";
import ImgsRS from "./ImgsRS";
import Gitlet from "../img/Gitlet.png";
import ImgsGL from "./ImgsGL";

function Index1Text() {
  return (
    <div className="p-wrapper">
      <p>
        On May 15, 2021, I discovered what would soon become a truly burning
        passion. I bought a MacBook and told myself, "I'm going to become a
        programmer!", without even really knowing what that meant.
      </p>
      <p>
        I began studying, took courses on my weekends, and just barely scraped
        together enough time to realize the level of satisfaction I could
        achieve by building a working piece of software. I also quickly realized
        that, if I was going to be able to really learn, I'd need more time; and
        so, just shy of a year later,
        <strong> I quit my job to study full-time.</strong>
      </p>
      <p>
        From then on, I've had a growth factor of N<sup>2</sup>. But just what
        have I created?
      </p>
    </div>
  );
}

function Tweet() {
  return (
    <div className="tweet-wrapper">
      <blockquote className="twitter-tweet">
        <p lang="en" dir="ltr">
          Starting a journey to a new career today. First Swift, then SwiftUI.
          Maybe 200 days from now I&#39;ll be applying for jobs. For now, I know
          what a variable is 🤓
          <a href="https://twitter.com/hashtag/100daysofswift?src=hash&amp;ref_src=twsrc%5Etfw">
            #100daysofswift
          </a>
          <a href="https://twitter.com/twostraws?ref_src=twsrc%5Etfw">
            @twostraws
          </a>
          Thank you for such great content, and all for free. I&#39;m sure your
          efforts have inspired many!
        </p>
        &mdash; Devon Martin (@realDevonMartin)
        <a href="https://twitter.com/realDevonMartin/status/1393722001009819649?ref_src=twsrc%5Etfw">
          May 16, 2021
        </a>
      </blockquote>
    </div>
  );
}

function Index1() {
  return (
    <div className="index-even" id="index-1">
      <div>
        <Header string="about me" />
        <Index1Text />
        <Tweet />
        <div className="index-button-wrapper text-center">
          <Link
            className="index-button"
            to="index-2"
            spy={true}
            smooth={true}
            offset={-60}
            duration={500}
          >
            Projects?
          </Link>
        </div>
      </div>
    </div>
  );
}

class ListNode {
  constructor(src) {
    this.src = src;
    this.last = null;
    this.next = null;
  }
}

class LinkedList {
  constructor(nodes) {
    if (nodes) {
      this.head = new ListNode(nodes.pop()["src"]);
      this.cur = this.head;
    }
    while (nodes.length > 0) {
      this.cur.next = new ListNode(nodes.pop()["src"]);
      this.cur.next.last = this.cur;
      this.cur = this.cur.next;
    }
    this.cur.next = this.head;
    this.head.last = this.cur;
    this.cur = this.head;
  }
  get current() {
    return this.cur.src;
  }
  get next() {
    this.cur = this.cur.next;
    return this.cur.src;
  }
  get last() {
    this.cur = this.cur.last;
    return this.cur.src;
  }
  get right() {
    return this.cur.next.src;
  }
  get left() {
    return this.cur.last.src;
  }
}

function ProjectPopup(props) {
  let id = props.openProjectPopup;
  let name = `${id === "RS" ? "RuneScape in C" : "Gitlet"}`;
  if (!id) {
    return <div className="open-project"></div>;
  }
  let allPics = { RS: ImgsRS, GL: ImgsGL };
  let pics = new LinkedList([...allPics[id]]);

  function getImgs() {
    return [$("#proj-pic-left"), $("#proj-pic"), $("#proj-pic-right")];
  }

  function click(direction) {
    let imgs = getImgs();
    let name = "slide-" + direction;
    imgs.forEach((img) => {
      img.toggleClass(name);
      img.toggleClass("slid");
    });
    setTimeout(() => {
      imgs.forEach((img) => {
        img.toggleClass(name);
        img.toggleClass("slid");
      });
      imgs[1].attr("src", `${direction === "left" ? pics.next : pics.last}`);
      imgs[0].attr("src", pics.left);
      imgs[2].attr("src", pics.right);
    }, 800);
  }

  return (
    <div className={`open-project active`}>
      <div className="open-project-container">
        <img id="proj-pic-left" src={pics.left} alt="" />
        <img
          src={`${pics.current}`}
          alt={`Example of ${name}.`}
          id="proj-pic"
        />
        <img id="proj-pic-right" src={pics.right} alt="" />
        <button onClick={() => click("left")} id="left"></button>
        <button onClick={() => click("right")} id="right"></button>
        <h2>{name} erferferferfer</h2>
        <hr style={{width: '100%'}}/>
      </div>
    </div>
  );
}

function ProjectInfo(props) {
  let id = props.id;

  let mouseover = () => {
    let object = document.getElementById(id);
    object.classList.add("visible");
    let children = object.children;
    children[0].classList.add("slide-down");
    children[1].classList.add("slide-up");
  };

  let mouseout = () => {
    let object = document.getElementById(id);
    object.classList.remove("visible");
    let children = object.children;
    children[0].classList.remove("slide-down");
    children[1].classList.remove("slide-up");
  };

  let darken = () => {
    document.getElementById("dark").classList.add("active");
    mouseout();
  };

  let projects = {};

  switch (id) {
    case "RS":
      projects["name"] = "runescape";
      projects["pic"] = RS;
      projects["alt"] = "RuneScape In C: Basic remake of MMORPG RuneScape";
      projects["title"] = "RuneScape In C";
      break;
    case "GL":
      projects["name"] = "gitlet";
      projects["pic"] = Gitlet;
      projects["alt"] = "Gitlet: lightweight remake of Git";
      projects["title"] = "Gitlet";
      break;
    default:
      return;
  }

  return (
    <div id={projects["name"]}>
      <img src={projects["pic"]} alt={projects["alt"]} />
      <div
        onMouseOver={() => mouseover()}
        onMouseOut={() => mouseout()}
        id={id}
      >
        <h3 className="slider">{projects["title"]}</h3>
        <button
          className="index-button project-button slider"
          onClick={() => {
            darken();
            props.setOpenProjectPopup(id);
          }}
        >
          Learn More
        </button>
      </div>
    </div>
  );
}

function Index2() {
  let [openProjectPopup, setOpenProjectPopup] = useState("");
  function closeDark() {
    $('dark').toggleClass('active')
    document.getElementById("dark").classList.remove("active");
    setOpenProjectPopup("");

  }

  return (
    <div className="index-odd" id="index-2">
      <div id="dark" onClick={closeDark}></div>
      <ProjectPopup openProjectPopup={openProjectPopup} />
      <Header string="noteworthy projects" />
      <div className="gallery">
        <ProjectInfo id="RS" setOpenProjectPopup={setOpenProjectPopup} />
        <ProjectInfo id="GL" setOpenProjectPopup={setOpenProjectPopup} />
      </div>
      <div className="p"></div>
    </div>
  );
}

function Home() {
  return (
    <div className="index-wrapper">
      <div id="top"></div>
      <Index1 />
      <Index2 />
    </div>
  );
}

export default Home;
