import React, { useState } from "react";
import "../css/styles.css";
import ky from "ky";

function Contact() {
  let [fullName, setFullName] = useState("");
  let [email, setEmail] = useState("");
  let [message, setMessage] = useState("");
  let [sent, setSent] = useState(false);

  let resetForm = () => {
    setFullName("");
    setEmail("");
    setMessage("");
    setTimeout(() => {
      setSent(false);
    }, 3000);
  };

  let handleSubmit = (e) => {
    e.preventDefault();
    let data = {
      name: fullName,
      email: email,
      message: message,
    };
    fetch("/api/email", {
      method: "POST",
      headers: {
        "Content-type": "application/json"
      },
      body: data
    })
    // ky.fet("/api/email", data)
    //   .then((res) => {
    //     setSent(true);
    //     resetForm();
    //   })
    //   .catch((e) => {
    //     console.log(e);
    //   });
  };

  return (
    <div>
      <div className="email-container">
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            name="name"
            id="name"
            placeholder="Full name"
            onChange={(e) => setFullName(e.target.value)}
            value={fullName.title}
          />
          <br />
          <input
            type="email"
            name="email"
            id="email"
            placeholder="Email address"
            onChange={(e) => setEmail(e.target.value)}
            value={email.title}
          />
          <br />
          <textarea
            name="message"
            id="message"
            cols="30"
            rows="10"
            placeholder="Your message"
            onChange={(e) => setMessage(e.target.value)}
            value={message.title}
          ></textarea>
          <br />
          <button type="submit">Submit</button>
        </form>
      </div>
    </div>
  );
}

export default Contact;
