import React, { useState } from "react";
import "../css/Contact.css";
import axios from "axios";

function SocialMedia(props) {
  return (
    <a href={props.link} target="_blank">
      <iconify-icon
        icon={props.id}
        width="35"
        height="35"
      ></iconify-icon>
    </a>
  );
}

function Contact() {
  let [fullName, setFullName] = useState("");
  let [email, setEmail] = useState("");
  let [phone, setPhone] = useState("");
  let [company, setCompany] = useState("");
  let [message, setMessage] = useState("");
  let [sent, setSent] = useState(false);

  let resetForm = () => {
    setFullName("");
    setEmail("");
    setPhone("");
    setCompany("");
    setMessage("");
    setTimeout(() => {
      setSent(false);
    }, 5000);
  };

  let handleSubmit = (e) => {
    e.preventDefault();
    let data = {
      name: fullName,
      email: email,
      phone: phone,
      company: company,
      message: message,
    };
    axios.post("/api/email", data).catch((e) => {
      console.log(e);
    });
    setSent(true);
    resetForm();
  };

  return (
    <div className="contact-page-container">
      <div className="contact-page-head">
        <iconify-icon
          icon="line-md:check-list-3"
          width="125"
          height="125"
        ></iconify-icon>
        <h1>So I've checked your boxes</h1>
        <p>
          Have an interesting project idea, or think I'd make a good addition to
          your development team? Get in touch, and let's talk about how we can
          work together!
        </p>
      </div>
      <div className="contact-container">
        <div className="email-container">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              id="name"
              placeholder="Your name"
              onChange={(e) => setFullName(e.target.value)}
              value={fullName}
            />
            <br />
            <input
              type="email"
              name="email"
              id="email"
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
              value={email}
              required
            />
            <br />
            <input
              type="tel"
              name="phone"
              id="phone"
              placeholder="Phone"
              onChange={(e) => setPhone(e.target.value)}
              value={phone}
            />
            <br />
            <input
              type="text"
              name="company"
              id="company"
              placeholder="Company"
              onChange={(e) => setCompany(e.target.value)}
              value={company}
            />
            <br />
            <textarea
              name="message"
              id="message"
              cols="30"
              rows="2"
              placeholder="Message"
              onChange={(e) => setMessage(e.target.value)}
              value={message}
            ></textarea>
            <br />
            <button type="submit">Send</button>
            <div className={`${sent ? "email-sent" : "hidden"}`}>
              Your message has been recieved!
            </div>
          </form>
        </div>
        <div className="social-media-container">
          <p>Find me (barely ever) on:</p>
          <div className="social-media">
            <SocialMedia link="https://www.facebook.com/devonfromfl" id="entpyo-social:facebook" />
            <iconify-icon
              icon="entypo-social:facebook"
              width="35"
              height="35"
            ></iconify-icon>
            <iconify-icon
              icon="akar-icons:instagram-fill"
              width="35"
              height="35"
            ></iconify-icon>
            <iconify-icon
              icon="entypo-social:twitter"
              width="35"
              height="35"
            ></iconify-icon>
            <iconify-icon
              icon="entypo-social:linkedin"
              width="35"
              height="35"
            ></iconify-icon>
            <iconify-icon
              icon="akar-icons:github-fill"
              width="35"
              height="35"
            ></iconify-icon>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Contact;
