import React, { useState } from "react";
import "../css/Contact.css";
import axios from 'axios';
import $ from 'jquery';

function Contact() {
  let [fullName, setFullName] = useState("");
  let [email, setEmail] = useState("");
  let [message, setMessage] = useState("");
  let [sent, setSent] = useState(false);

  let resetForm = () => {
    setFullName("");
    setEmail("");
    setMessage("");
    setTimeout(() => {
      setSent(false);
    }, 3000);
  };

  let handleSubmit = (e) => {
    e.preventDefault();
    let data = {
      name: fullName,
      email: email,
      message: message,
    };
    axios.post('/api/email', data)
      .catch((e) => {
        console.log(e);
      });
        setSent(true);
        resetForm();
  };

  return (
    <div className="contact-page-container">
      <div className="contact-page-head">
        <iconify-icon
          icon="line-md:check-list-3"
          width="125"
          height="125"
        ></iconify-icon>
        <h1>So I've checked your boxes</h1>
        <p>
          Have an interesting project idea, or think I'd make a good addition to
          your development team? Get in touch, and let's talk about how I can
          help!
        </p>
      </div>
      <div className="contact-container">
        <div className="email-container">
          <form onSubmit={handleSubmit}>
            <input
              type="text"
              name="name"
              id="name"
              placeholder="Your name"
              onChange={(e) => setFullName(e.target.value)}
              value={fullName}
            />
            <br />
            <input
              type="email"
              name="email"
              id="email"
              placeholder="Email"
              onChange={(e) => setEmail(e.target.value)}
              value={email}
              required
            />
            <br />
            <input
              type="tel"
              name="phone"
              id="phone"
              placeholder="Phone"
              onChange={(e) => setPhone(e.target.value)}
              value={phone}
            />
            <br />
            <input
              type="text"
              name="company"
              id="company"
              placeholder="Company"
              onChange={(e) => setCompany(e.target.value)}
              value={company}
            />
            <br />
            <textarea
              name="message"
              id="message"
              cols="30"
              rows="2"
              placeholder="Your message"
              onChange={(e) => setMessage(e.target.value)}
              value={message}
            ></textarea>
            <br />
            <button type="submit">Send</button>
            <div className={`${sent ? "email-sent" : "hidden"}`}>
              Your message has been recieved!
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default Contact;
