import React from "react";
import { Link } from "react-scroll";
import "../css/Home.css";
import Header from "../components/Header";
import "https://platform.twitter.com/widgets.js";
import RS from "../img/RuneScapeInC.png";
import Gitlet from "../img/Gitlet.png";

function Index1Text() {
  return (
    <div className="p-wrapper">
      <p>
        On May 15, 2021, I discovered what would soon become a truly burning
        passion. I bought a MacBook and told myself, "I'm going to become a
        programmer!", without even really knowing what that meant.
      </p>
      <p>
        I began studying, took courses on my weekends, and just barely scraped
        together enough time to realize the level of satisfaction I could
        achieve by building a working piece of software. I also quickly realized
        that, if I was going to be able to really learn, I'd need more time; and
        so, just shy of a year later,
        <strong> I quit my job to study full-time.</strong>
      </p>
      <p>
        From then on, I've had a growth factor of N<sup>2</sup>. But just what
        have I created?
      </p>
    </div>
  );
}

function Tweet() {
  return (
    <blockquote className="twitter-tweet">
      <p lang="en" dir="ltr">
        Starting a journey to a new career today. First Swift, then SwiftUI.
        Maybe 200 days from now I&#39;ll be applying for jobs. For now, I know
        what a variable is 🤓
        <a href="https://twitter.com/hashtag/100daysofswift?src=hash&amp;ref_src=twsrc%5Etfw">
          #100daysofswift
        </a>
        <a href="https://twitter.com/twostraws?ref_src=twsrc%5Etfw">
          @twostraws
        </a>
        Thank you for such great content, and all for free. I&#39;m sure your
        efforts have inspired many!
      </p>
      &mdash; Devon Martin (@realDevonMartin)
      <a href="https://twitter.com/realDevonMartin/status/1393722001009819649?ref_src=twsrc%5Etfw">
        May 16, 2021
      </a>
    </blockquote>
  );
}

function Index1() {
  return (
    <div className="index-even" id="index-1">
      <div>
        <Header string="about me" />
        <Index1Text />
        <div className="tweet-wrapper">
          <Tweet />
        </div>
        <div className="index-button-wrapper text-center">
          <Link
            className="index-button"
            to="index-2"
            spy={true}
            smooth={true}
            offset={-60}
            duration={500}
          >
            Projects?
          </Link>
        </div>
      </div>
    </div>
  );
}

function Project(id) {
  let mouseover = (id) => {
    document.getElementById(id).classList.add("visible");
  };

  let mouseout = (id) => {
    document.getElementById(id).classList.remove("visible");
  };

  let openProject = (id) => {
    document.getElementById("dark").classList.add("active");
    document.getElementById(id).classList.remove("visible");
    // this.dispatchEvent(new MouseEvent('mouseout'));
  };

  const projects = {
    'RS': {
      'name': "runescape",
      'alt': "RuneScape In C: Basic remake of MMORPG RuneScape",
      'pic': RS,

    },
    'GL': {
      'name': "gitlet"
    },
  };

  return (
    <div id={projects[RS['name']]}>
      <img src={RS} alt="RuneScape In C: Basic remake of MMORPG RuneScape" />
      <div
        onMouseOver={() => mouseover("RS")}
        onMouseOut={() => mouseout("RS")}
        id="RS"
      >
        <h3>RuneScape in C</h3>
        <button
          className="index-button"
          onClick={() => {
            openProject("RS");
          }}
        >
          Learn More
        </button>
      </div>
    </div>
  );
}

function Index2() {
  return (
    <div className="index-odd" id="index-2">
      <Header string="noteworthy projects" />
      <div className="gallery">
        {/* <div id="runescape">
          <img
            src={RS}
            alt="RuneScape In C: Basic remake of MMORPG RuneScape"
          />
          <div
            onMouseOver={() => mouseover("RS")}
            onMouseOut={() => mouseout("RS")}
            id="RS"
          >
            <h3>RuneScape in C</h3>
            <button
              className="index-button"
              onClick={() => {
                openProject("RS");
              }}
            >
              Learn More
            </button>
          </div>
        </div>
        <div id="gitlet">
          <img src={Gitlet} alt="Gitlet: lightweight remake of Git" />
          <div>
            <h3>Gitlet</h3>
            <button
              className="index-button"
              onClick={() => {
                openProject("gitlet");
              }}
            >
              Learn More
            </button>
          </div>
        </div> */}
      </div>
      <div className="p"></div>
    </div>
  );
}

function Home() {
  return (
    <div className="index-wrapper">
      <div id="top"></div>
      <Index1 />
      <Index2 />
    </div>
  );
}

export default Home;
